<style scoped>
.contact {
    width: 100%;
}
.container {
    margin: 0 auto;
    max-width: 1200px;
    padding: 20px;
}
.contact-text {
    margin-top: 100px;
}
h2  {
    color: var(--bg);
    position: relative;
    margin-bottom: 50px;
    font-size: 4rem;
    -webkit-text-stroke: 0.3px var(--color);
    text-shadow: 1px 1px 6px var(--color);
}
h2::after  {
    content: '';
    position: absolute;
    width: 90px;
    height: 90px;
    background-color: rgb(25, 32, 57, 0.6);
    border-radius: 5px;
    left: 10px;
    transform: rotate(30deg);
    z-index: -1;
    border: 0.5px solid var(--color);
}
h2::before  {
    content: '';
    position: absolute;
    width: 80px;
    height: 80px;
    background-color: rgb(25, 32, 57, 0.6);
    border-radius: 5px;
    left: 55px;
    z-index: -1;
    transform: rotate(15deg);
    border: 0.5px solid var(--color);
}
.contact-info {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 50px;
}
.phone {
    display: flex;
    flex-direction: column;
    align-items: center;
    color: white;
    gap: 30px;
    text-align: center;
    margin-top: 30px;
}
h3 {
    font-size: 2rem;
}
h4 {
    font-size: 5rem;
}

.contact-card {
    position: relative;
    padding: 20px;
    border-radius: 3px;
    color: white;
    font-size: 35px;
    width: 100%;
    display: flex;
    justify-content: space-evenly;
}
.contact-card svg {
    fill: var(--color);
    width: 50px;
    height: 50px;
    transition: 0.5s;
}
.contact-card svg:hover {
    transform: scale(1.2);
    -webkit-filter: drop-shadow(0px 0px 5px var(--color));
    filter: drop-shadow(0px 0px 5px var(--color));
}
@media all and (max-width: 600px) {
    h2  {
        font-size: 2.5rem;
    }
    h3 {
        font-size: 1rem;
    }
    h4 {
        font-size: 2rem;
    }
    .contact-card svg {
        width: 40px;
        height: 40px;
    }
}
</style>